"""
  根据代码号和行号获得详细信息
"""
def getErrorInfo(scriptNo,lineNo):
  line =scripts[scriptNo][1][lineNo]
  cmdCode = line[0]
  opr = ops[cmdCode]
  line = list(line)
  line[0] = opr
  line = tuple(line)
  return "错误的代码："+str(line);

